double Arm::NativeUnitsToRadSec(double nativeUnits) {
	double nativeUnitsPerSec = nativeUnits * 10.0;
	double rotationsPerSec = nativeUnitsPerSec / ENCODER_COUNTS_PER_REV;

	return rotationsPerSec * (2*PI);
}

